Widget _buildLocationCard(ThemeData theme, bool isTablet) {
  return Container(
    padding: EdgeInsets.all(isTablet ? 24 : 20),
    decoration: BoxDecoration(
      color: theme.colorScheme.surface,
      borderRadius: BorderRadius.circular(16),
      border: Border.all(
        color: theme.colorScheme.outline.withOpacity(0.2),
        width: 1,
      ),
      boxShadow: [
        BoxShadow(
          color: theme.colorScheme.shadow.withOpacity(0.05),
          blurRadius: 10,
          offset: const Offset(0, 2),
        ),
      ],
    ),
    child: Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        // T铆tulo de la secci贸n
        Row(
          children: [
            Icon(
              Icons.location_on,
              color: theme.colorScheme.primary,
              size: isTablet ? 24 : 20,
            ),
            const SizedBox(width: 12),
            Text(
              'Ubicaci贸n',
              style: TextStyle(
                fontSize: isTablet ? 18 : 16,
                fontWeight: FontWeight.bold,
                color: theme.colorScheme.primary,
              ),
            ),
          ],
        ),
        const SizedBox(height: 16),

        // Direcci贸n completa
        if (ranch.address!.addresses.isNotEmpty) ...[
          Text(
            ranch.address!.addresses,
            style: TextStyle(
              fontSize: isTablet ? 16 : 14,
              color: theme.colorScheme.onSurface,
              fontWeight: FontWeight.w500,
            ),
          ),
          const SizedBox(height: 8),
        ],

        // Ciudad, Estado, Pa铆s
        Row(
          children: [
            Icon(
              Icons.location_city,
              size: isTablet ? 16 : 14,
              color: Colors.grey[600],
            ),
            const SizedBox(width: 6),
            Expanded(
              child: Text(
                [
                  if (ranch.address!.cityName != null)
                    ranch.address!.cityName!,
                  if (ranch.address!.stateName != null)
                    ranch.address!.stateName!,
                  if (ranch.address!.countryName != null)
                    ranch.address!.countryName!,
                ].join(', '),
                style: TextStyle(
                  fontSize: isTablet ? 14 : 13,
                  color: Colors.grey[700],
                ),
              ),
            ),
          ],
        ),

        // Parroquia (si existe)
        if (ranch.address!.parishName != null &&
            ranch.address!.parishName!.isNotEmpty) ...[
          const SizedBox(height: 6),
          Row(
            children: [
              Icon(
                Icons.map,
                size: isTablet ? 16 : 14,
                color: Colors.grey[600],
              ),
              const SizedBox(width: 6),
              Text(
                'Parroquia: ${ranch.address!.parishName}',
                style: TextStyle(
                  fontSize: isTablet ? 14 : 13,
                  color: Colors.grey[600],
                ),
              ),
            ],
          ),
        ],
      ],
    ),
  );
}
